CREATE PROCEDURE [DTG].[ins_TableColumnDraft]
(
	@TableDraftId INT,
	@TermId INT = NULL,
	@ColumnName VARCHAR(100),
	@IsKey TINYINT,
	@DataTypeChanged TINYINT = NULL,
	@DataType VARCHAR(50),
	@Nullable TINYINT = NULL,
	@Sensitivity TINYINT = NULL,
	@Description VARCHAR(500) = NULL,
	@IsReference TINYINT = 0,
	@IsIdentity TINYINT = 0,
	@TableColumnCatalogId INT = NULL
)
AS
	
SET NOCOUNT ON
	BEGIN

	DECLARE @InsertedId INT

	INSERT INTO [DTG].[TableColumnDraft]
	(
		TableDraftId,
		TermId,
		ColumnName,
		IsKey,
		DataTypeChanged,
		DataType,
		Nullable,
		Sensitivity,
		[Description],
		IsReference,
		IsIdentity,
		TableColumnCatalogId
	)
	VALUES
	(
		@TableDraftId,
		@TermId,
		@ColumnName,
		@IsKey,
		@DataTypeChanged,
		@DataType,
		@Nullable,
		@Sensitivity,
		@Description,
		@IsReference,
		@IsIdentity,
		@TableColumnCatalogId
	)

	SELECT @InsertedId = CAST(SCOPE_IDENTITY() AS INT)

	IF @TermId IS NOT NULL
	BEGIN
		IF NOT EXISTS(SELECT ColumnRuleId 
						FROM DTG.ColumnRule WITH (NOLOCK) 
					   WHERE TermId = @TermId)
		BEGIN
			INSERT INTO DTG.ColumnRule(TermId, DataType, ColumnName)
			VALUES(@TermId, @DataType, @ColumnName)
		END
	END

	SELECT @InsertedId

	END