CREATE PROCEDURE [DTG].[sel_SchemaNameFromCatalog]
	@DBName VARCHAR(20) = NULL
AS
	SET NOCOUNT ON

	SELECT DISTINCT
		TBL.SchemaName
	FROM [DTG].[Table] AS TBL WITH (NOLOCK)
	WHERE 
		((@DBName IS NULL) OR 
			TBL.DBName = @DBName)
	UNION
	SELECT DISTINCT
		TBL.SchemaName
	FROM [DTG].[TableDraft] AS TBL WITH (NOLOCK)
	WHERE 
		((@DBName IS NULL) OR 
			TBL.DBName = @DBName)
	ORDER BY TBL.SchemaName