CREATE PROCEDURE [DTG].[sel_TableDraft]
AS
	 
SET NOCOUNT ON

	SELECT
		TBL.TableDraftId,
		TBL.DBName,
		TBL.SchemaName,
		TBL.TableName,
		TBL.TermId,
		T.Name AS TermName,
		T.Description AS TermDescription, 
		T.Level1Domain AS TermLevel1Domain,
		T.Level2Domain AS TermLevel2Domain,
		TBL.IsEntity,
		TBL.[Description],
		TBL.[Status],
		U.[Name] AS UserName,
		U2.[Name] AS UpdateUserName,
		TBL.DevVersion,
		TBL.TestVersion,
		TBL.PrepVersion,
		TBL.ProdVersion,
		TBL.LogTableName,
		TBL.TableType,
		TBL.DevelopmentLocation
  FROM [DTG].[TableDraft] AS TBL WITH (NOLOCK)
	LEFT JOIN DTG.Term AS T WITH (NOLOCK)
		ON T.TermId = TBL.TermId
	LEFT JOIN [DTG].[vUser] U WITH (NOLOCK)
		ON TBL.UserName = U.UserCode
    LEFT JOIN [DTG].[vUser] U2 WITH (NOLOCK)
		ON TBL.UpdateUserName = U2.UserCode
 ORDER BY TBL.SystemDate DESC